var date = current.event_end.getDisplayValue();;
//Split the date to get the YYYYMMDDTHHMMZ
	var month = date.substr(5,2);
	var day = date.substr(8,2);
	var year = date.substr(0,4);
	var hr = date.substr(11,2);
	var min = date.substr(14,2);
	current.dtend = year + month + day  + 'T' + hr + min + '00';

